// n8n Function node: Normalize AI output to fixed schema

// Expected schema:
// {
//   title_of_meeting: string,
//   date_of_meeting: string (YYYY-MM-DD),
//   attendees: [string],
//   summary: string,
//   action_items: [
//     { task: string, responsible_party: string, due_date: string }
//   ],
//   airtable_tasks: string,
//   decisions: [string]
// }

const raw = $input.first().json.output;

// Helper: normalize array of strings
function normalizeStringArray(value) {
  if (Array.isArray(value)) return value.map(String);
  if (typeof value === "string" && value.trim() !== "") return [value];
  return [];
}

// Helper: normalize action items
function normalizeActionItems(items) {
  if (!Array.isArray(items)) return [];
  return items.map(item => ({
    task: item.task || item.name || item.title || "",
    responsible_party: item.responsible_party || item.assignee || item.owner || "",
    due_date: item.due_date || item.deadline || ""
  }));
}

// Build normalized object
const normalized = {
  title_of_meeting: raw.title_of_meeting || raw.title || raw.meeting_title || "",
  date_of_meeting: raw.date_of_meeting || raw.date || raw.meeting_date || "",
  attendees: normalizeStringArray(raw.attendees || raw.participants),
  summary: raw.summary || raw.meeting_summary || "",
  action_items: normalizeActionItems(raw.action_items || raw.tasks || raw.airtable_tasks_list || []),
  airtable_tasks: raw.airtable_tasks || raw.tasks_for_airtable || "",
  decisions: normalizeStringArray(raw.decisions || raw.outcomes)
};

return [{ json: normalized }];
